import styles from './Article.module.css'
import React from "react";


class Article extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            'ccy':'',
            'currencyRate': {}
        }
        this.currency = ['USD', 'EUR', 'RUR']
        this.getRate()
    }

    getRate = () => {
        fetch('https://api.privatbank.ua/p24api/pubinfo?json&exchange&coursid=5')
            .then(data => {
                return data.json()
            })

            .then(data => {
                console.log(data);
                this.setState(data)
            
            })
    }

    render() {

        return (
            <div className={styles.Article}>
                <span className={styles.item1}> Курс валют на   </span>
                <span className={styles.item2}>USD </span>
                <span className={styles.item3}>USD </span>
                <span className={styles.item4}>USD </span>
                <span className={styles.item5}>Калькулятор обмена </span>
                <span
                    className={styles.item6}>Я хочу: Купить/продать, Результат </span>
            </div>
        )
    }
}

export default Article
